import React, { useState, useEffect } from "react";
import axios from "axios";
import Todo from "./Todo";

const API_URL = "http://localhost:5000/todos";

function App() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState("");

  // ✅ Fetch To-Do list from backend
  useEffect(() => {
    axios.get(API_URL)
      .then((res) => setTodos(res.data))
      .catch((err) => console.error("❌ Error fetching todos:", err.response?.data || err.message));
  }, []);

  // ✅ Add new To-Do
  const addTodo = () => {
    if (!newTodo.trim()) return;
    axios.post(API_URL, { text: newTodo })
      .then((res) => {
        setTodos([...todos, res.data]);
        setNewTodo("");
      })
      .catch((err) => console.error("❌ Error adding todo:", err.response?.data || err.message));
  };

  // ✅ Delete a To-Do
  const deleteTodo = (id) => {
    axios.delete(`${API_URL}/${id}`)
      .then(() => {
        setTodos(todos.filter(todo => todo._id !== id));
      })
      .catch((err) => console.error("❌ Error deleting todo:", err.response?.data || err.message));
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>To-Do List</h1>
      <input
        type="text"
        value={newTodo}
        onChange={(e) => setNewTodo(e.target.value)}
        placeholder="Enter a new task"
      />
      <button onClick={addTodo}>Add</button>
      <ul>
        {todos.map((todo) => (
          <Todo key={todo._id} todo={todo} onDelete={deleteTodo} />
        ))}
      </ul>
    </div>
  );
}

export default App;
